import
    React from "react";
import {withSnackbar} from "notistack";
import {withRouter} from "react-router-dom";
import {withStyles} from "@material-ui/core/styles";
import GridList from '@material-ui/core/GridList';
import GridListTile from '@material-ui/core/GridListTile';
import {Container, Toolbar, Typography, Button, Grid, TextField} from "@material-ui/core";
import {inject, observer} from "mobx-react";


const styles = theme => ({
    mainContainer: {
        flexGrow: 1,
        marginTop:20,
        maxWidth:'100%',
    },
    appBarSpacer: theme.mixins.toolbar,
    mainContent: {
        display: 'flex',
        flexDirection: 'column',
        alignItems: 'center',
    },
    toolbar: {
        width: '100%',
        padding:0,
    },
    buttonType1:{
        width: 100,
        marginRight: theme.spacing(2),
    },
    buttonType2:{
        width: 150,
        float:'right',

    },
    toolButton:{
        border:'1px solid black',
        height:50,
        width:'100%',
    },
    test:{
        border:'1px solid black',
        height: '50%',
    },
    toolBox:{
        border:'1px solid black',
        marginRight: 1,
        height:'100%',
    },
    canvas:{
        backgroundColor:'black',
    },
});


@inject('fileUploadStore')
@observer
class BoundaryBox extends React.Component {
    componentDidMount() {
        this.props.enqueueSnackbar("BoundaryBox Work", {
            variant: 'info'
        });
    }
    fileUploadHandler = (event) => {
        this.props.fileUploadStore.fileUpload();
    }

    render() {
        const { classes } = this.props;

        return (
            <Container component="main" className={classes.mainContainer}>
                {/*Stepper*/}
                <div style={{marginTop:70}}>
                </div>
                <div className={classes.appBarSpacer} />
                <div className={classes.mainContent}>
                    <Toolbar className={classes.toolbar}>
                        <Grid container>
                            <Grid item xs={1} style={{marginRight:5}}>
                                <div>
                                    <input type="file" name="file"  onChange={this.fileUploadHandler}/>
                                </div>
                            </Grid>
                            <Grid item xs={1} style={{marginRight:5}}>
                                <Button
                                    type="submit"
                                    variant="contained"
                                    color="primary"
                                    className={classes.toolButton} >
                                    Open DIR
                                </Button>
                            </Grid>
                            <Grid item xs={1} style={{marginRight:5}}>
                                <Button
                                    type="submit"
                                    variant="contained"
                                    color="primary"
                                    className={classes.toolButton} >
                                    Change Save Dir
                                </Button>
                            </Grid>
                            <Grid item xs={1} style={{marginRight:5}}>
                                <Button
                                    type="submit"
                                    variant="contained"
                                    color="primary"
                                    className={classes.toolButton} >
                                    Zoom In
                                </Button>
                            </Grid>
                            <Grid item xs={1} style={{marginRight:5}}>
                                <Button
                                    type="submit"
                                    variant="contained"
                                    color="primary"
                                    className={classes.toolButton} >
                                    Zoom Out
                                </Button>
                        </Grid>
                        </Grid>
                        {/*Img*/}
                    </Toolbar>
                    <hr></hr>
                    <Grid container>
                        <Grid item xs={1}>
                            <div className={classes.toolBox}>
                                Tool Box
                            </div>
                        </Grid>
                        <Grid item xs={8} className={classes.canvas}>
                            <img src="https://placeimg.com/1000/800/any" alt={''} />
                        </Grid>
                        <Grid item xs={3}>
                            <div className={classes.test}>
                            <Typography>
                                작업리스트
                            </Typography>
                                <hr></hr>
                            </div>

                            <div className={classes.test}>
                            <Typography>
                                완료리스트
                            </Typography>
                            <hr></hr>
                            </div>
                        </Grid>
                    </Grid>
                </div>
                <div>
                    <hr></hr>
                    <Button
                        type="submit"
                        className={classes.buttonType1}
                        variant="outlined"
                        onClick={this.handleSubmitForm} >
                        Previous
                    </Button>
                    <Button
                        type="submit"
                        className={classes.buttonType1}
                        variant="outlined"
                        onClick={this.handleSubmitForm} >
                        Next
                    </Button>
                    <Button
                        type="submit"
                        className={classes.buttonType2}
                        color="primary"
                        variant="outlined"
                        onClick={this.handleSubmitForm} >
                        Save and Next
                    </Button>
                </div>
            </Container>
        );
    }
};

export default withSnackbar(withRouter(withStyles(styles) (BoundaryBox)));
