import React from "react";
import {withSnackbar} from "notistack";
import {withRouter} from "react-router-dom";
import {withStyles} from "@material-ui/core/styles";
import {
    Avatar,
    Button,
    Container,
    Grid,
    Typography,
    DialogContent,
    DialogActions
} from "@material-ui/core";

import axios from "axios";
import fileDownload from "js-file-download";
import {inject, observer} from "mobx-react";
import {JanusDemo} from "./JanusDemo";
import * as Params from "../common/Params";
import DialogTitle from "@material-ui/core/DialogTitle/DialogTitle";
import FormControl from "@material-ui/core/FormControl";
import RadioGroup from "@material-ui/core/RadioGroup/RadioGroup";
import FormControlLabel from "@material-ui/core/FormControlLabel/FormControlLabel";
import Radio from "@material-ui/core/Radio/Radio";
import Select from "@material-ui/core/Select/Select";
import MenuItem from "@material-ui/core/MenuItem";
import Dialog from "@material-ui/core/Dialog/Dialog";
import {browserType, deviceType} from "../common/Params";

const ContentWidth = 1440;

const styles = theme => ({
    mainContainer: {
        width: '100%',
        textAlign: 'center',
        padding: 0,
        maxWidth: 'initial',
    },
    appBarSpacer: theme.mixins.toolbar,
    mainContent: {
        width: '100%',
        display: 'flex',
        flexDirection: 'column',
    },
    grow: {
        flexGrow: 1,
    },
    titleContainer: {
        // paddingTop: 120,
        // paddingBottom: 120,
        // maxWidth: ContentWidth,
        height:'800px',
        width:'100%',
        display:'flex',
        backgroundImage: 'url(/images/bg.png)',
        backgroundRepeat: 'no-repeat',
        backgroundSize: 'cover',
        backgroundPosition:'center',
        [theme.breakpoints.down("sm")]: {
            height:'auto',
            padding:36,
        },
    },
    titleContainerSlogan:{
        maxWidth: ContentWidth,
        margin:'0 auto',
        padding:'0 6vw',
        display:'flex',
        alignItems:'center',
        justifyContent:'center',
        flexDirection:'column',
    },
    title1Container: {

    },
    title1: {
        fontSize: 52,
        fontWeight: 800,
        lineHeight: 1.2,
        color: '#333333',
        wordBreak: 'keep-all',
        [theme.breakpoints.down("sm")]: {
            fontSize:'2.3em',
        },
    },
    title2: {
        fontSize: 48,
        color: '#333333',
        [theme.breakpoints.down("sm")]: {
            fontSize:'2em',
        },
    },
    title3Container: {
        paddingTop: 52,
    },
    title3: {
        fontSize: 16,
        color: 'rgba(0, 0, 0, 0.46)',
        [theme.breakpoints.down("sm")]: {
            fontSize:'0.8em',
        },
    },
    startButtonContainer: {
        paddingTop: 92,
        [theme.breakpoints.down("sm")]: {
            paddingTop: 60,
        },
    },
    startTeacherButton: {
        margin: 12,
        width: 165,
        height: 48,
        fontSize: 16,
        borderRadius: 24,
        color: '#FFFFFF',
        backgroundColor: '#3c3c3c',
        '&:hover':{
            backgroundColor:'#1f1f1f'
        },
        [theme.breakpoints.down("sm")]: {
            marginBottom:'1em',
        },
    },
    startStudentButton: {
        margin:12,
        width: 165,
        height: 48,
        fontSize: 16,
        borderRadius: 24,
    },

    qrContainer: {
        paddingTop: 106,
        paddingRight: theme.spacing(3),
        textAlign: 'right',
    },

    contentContainer: {
        maxWidth: ContentWidth,
        width: '100%',
        margin:'0 auto',
        backgroundColor: '#fafafa',
    },
    content1Container: {
        paddingTop: 80,
        paddingBottom: 20,
    },
    content1PrevNextImg: {
        width: 61,
        height: 121,
        '@media screen and (max-width: 960px)': {
            width: 30,
            height: 60,
        },
        '@media screen and (max-width: 600px)': {
            width: 15,
            height: 30,
        },
    },
    content1: {
        fontSize: 32,
        fontWeight: 700,
        color: '#333333',
        [theme.breakpoints.down("sm")]: {
            fontSize:'2em',
            padding:'0 4vw'
        },
    },
    visibleContent2: {

    },
    noVisibleContent2: {
        display: 'none',
    },
    content2AvatarContainer: {
        display: 'flex',
        justifyContent: 'center',
        marginTop: 32,
    },
    content2Avatar: {
        width: 30,
        height: 30,
        backgroundColor: '#cf1a1a',
    },
    content2TitleContainer: {
        marginTop: 22,
    },
    content2Title: {
        fontSize: 28,
        fontWeight: 700,
        color: '#333333',
    },
    content2ContentContainer: {
        marginTop: 16,
    },
    content2Content: {
        fontSize: 20,
        color: '#333333',
    },
    content2ImageContainer: {
        marginTop: 56,
        minHeight: 570,
        '@media screen and (max-width: 960px)': {
            minHeight: 405,
        },
        '@media screen and (max-width: 600px)': {
            minHeight: 252,
        },
    },
    content2Image: {
        width: '100%',
    },

    demo: {
        maxWidth: ContentWidth,
        margin:'0 auto',
        //backgroundColor: '#f4f4f4',
        paddingTop: 90,
        paddingBottom: 80,
    },
    demoTitleContainer: {
        paddingBottom: 60,
    },
    demoTitle: {
        fontSize: 32,
        fontWeight: 700,
        color: '#333333',
        [theme.breakpoints.down("sm")]: {
            fontSize:'2em',
            padding:'0 4vw'
        },
    },
    demoComment: {
        paddingTop: 16,
        paddingBottom: 8,
        fontSize: 22,
    },
    demoTeacher: {
        width: 500,
        height: 277,
        backgroundImage: 'url(/images/img-teacher-view-k.png)',
        display: 'flex',
        justifyContent: 'start',
        alignItems: 'start',
    },
    demoTeacherVideo: {
        width: 384,
        height: 214,
        marginTop: 35,
        marginLeft: 66,
        backgroundColor: 'black',
    },
    demoStudent: {
        width: 500,
        height: 277,
        backgroundImage: 'url(/images/img-student-view-k.png)',
        display: 'flex',
        justifyContent: 'start',
        alignItems: 'start',
        padding: 0,
    },
    demoStudentVideo: {
        width: 384,
        height: 214,
        marginTop: 35,
        marginLeft: 66,
        backgroundColor: 'black',
    },
    demoButtonContainer: {
        paddingTop: 16,
    },
    demoButton: {
        width: 300,
        height: 60,
