import React from "react";
import {withSnackbar} from "notistack";
import {withRouter} from "react-router-dom";
import {withStyles} from "@material-ui/core/styles";
import axios from "axios";
import {Container, Toolbar, Typography, Button} from "@material-ui/core";


const styles = theme => ({
    mainContainer: {
        flexGrow: 1,
        padding: theme.spacing(3),
    },
    appBarSpacer: theme.mixins.toolbar,
    mainContent: {
        marginTop: theme.spacing(2),
        display: 'flex',
        flexDirection: 'column',
        alignItems: 'center',
    },
    toolbar: {
        width: '100%',
    },
    successContainer: {
        marginTop : 200,
        margin: 'auto',
        textAlign: 'center',
    },
    successTitle: {
        fontSize: 38,
        color: '#333333',
        paddingTop: theme.spacing(6),
    },
    successHeader: {
        fontSize: 16,
        color: '#333333',
        paddingTop: theme.spacing(5),
    },
    successHeader2: {
        fontSize: 16,
        color: '#333333',
    },
    successBody: {
        fontSize: 14,
        color: '#b7b7b7',
        paddingTop: theme.spacing(1),
    },
    successButton: {
        borderRadius: 24,
        fontSize: 16,
        width: 200,
        height: 48,
        marginTop: theme.spacing(4),
    },
});


class Verify extends React.Component {
    handleChange(event) {
        this.setState({ file: event.target.files })
    }

    async handleSubmit(e) {
        e.preventDefault()
        let res = await this.uploadFile(this.state.file);
    }

    async uploadFile(File){
        const formData = new FormData();

        formData.append('image', File)
        axios.post('/api/v1/img/uploadMultipleFiles', formData, {
            headers: {
                'content-type': 'multipart/form-data'
            }
        }).then(response => {
            console.log(response.data)
        });
    }

    render()
    { const { classes } = this.props;

        return (
            <div className={classes.successContainer}>
            <form onSubmit={this.handleSubmit}>
                <input type="file" id="file" multiple name="file" onChange={this.handleChange} />
                <button type="submit" className="btn btn-info"> Update File </button>
            </form>
            </div>
        )
    }
};

export default withSnackbar(withRouter(withStyles(styles) (Verify)));
