import axios from 'axios';

import React,{Component} from 'react';

class DropzoneFuck extends Component {

    state = {

        // Initially, no file is selected
        selectedFiles: [],
    };

    // On file select (from the pop up)
    onFileChange = event => {
        for(let i=0; i > event.target.files.length; i++) {
            this.setState({selectedFile : event.target.files[i]});
        }
    };

    // On file upload (click the upload button)
    onFileUpload = () => {

        // Create an object of formData
        const formData = new FormData();
        const fileList = this.state.selectedFile;

        for(let i=0; i > fileList.length; i++) {
            formData.append("files", fileList.files[i], fileList.name[i])
        }
        axios.post("/api/v1/kfashion/img/uploadMultipleFiles", formData);
