import axios from 'axios';

import React,{Component} from 'react';

class DropzoneFuck extends Component {

    state = {

        // Initially, no file is selected
        selectedFile: [],
    };

    // On file select (from the pop up)
    onFileChange = event => {
        for(let i=0; i < event.target.files.length; i++) {
            this.setState({selectedFile : event.target.files[i]});
        }
    };

    // On file upload (click the upload button)
    onFileUpload = () => {

        // Create an object of formData
        const formData = new FormData();
        const fileList = this.state.selectedFile;

        for(let i=0; i < fileList.length; i++) {
            formData.append("files", fileList.files[i], fileList.name[i])

        }
        // Details of the uploaded file
        console.log(fileList);

        // Request made to the backend api
        // Send formData object
        axios.post("/api/v1/kfashion/img/uploadMultipleFiles", formData);
    };

    // File content to be displayed after
    // file upload is complete
    fileData = () => {

        if (this.state.selectedFile) {

            return (
                <div>
                    <h2>File Details:</h2>
                    <p>File Name: {this.state.selectedFile.name}</p>
                    <p>File Type: {this.state.selectedFile.type}</p>
                    <p>
                        Last Modified:{" "}
                        {this.state.selectedFile.lastModifiedDate.toDateString()}
                    </p>
                </div>
            );
        } else {
            return (
                <div>
                    <br />
                    <h4>Choose before Pressing the Upload button</h4>
                </div>
            );
        }
    };

    render() {

        return (
            <div>
                <div>
                    <input type="file" onChange={this.onFileChange} multiple />
                    <button onClick={this.onFileUpload}>
                        Upload!
                    </button>
                </div>
                {this.fileData()}
            </div>
        );
    }
}

export default DropzoneFuck;
