import React, { Component } from 'react'
import {DropzoneArea, DropzoneDialog} from 'material-ui-dropzone'
import Button from '@material-ui/core/Button';
import {withSnackbar} from "notistack";
import {withRouter} from "react-router-dom";
import {withStyles} from "@material-ui/core/styles";
import axios from "axios";
import {inject, observer} from "mobx-react";
import { DropzoneAreaBase } from 'material-ui-dropzone';

const styles = theme => ({
    toolButton:{
        border:'1px solid black',
        height:50,
        width:'100%',
    },
    toolBox:{
        border:'1px solid black',
        marginRight: 1,
        height:'100%',
    },
});
DropzoneDialog.defaultProps = {
    clearOnUnmount: true,
    filesLimit: 20,
    initialFiles: [],
};

@inject('fileUploadStore')
@observer
class DropzoneDialogExample extends Component {
    constructor(props) {
        super(props);
        this.state = {
            open: false,
            files: [],
        };
    }

    handleClose() {
        this.setState({
            open: false
        });
    }
      handleChange(files) {
   //Saving files to state for further use and closing Modal.
           this.props.fileUploadStore.changeUploadFile(files)
       }
    handleSave(file){
        this.setState({
            open: false,
            files:file
        });
        this.props.fileUploadStore.fileupload(file);
    }

    handleOpen() {
        this.setState({
            open: true,
        });
    }

    render() {
        const { classes } = this.props;
        const {uploadFile} = this.props.fileUploadStore;
        return (
            <div>
                <Button onClick={this.handleOpen.bind(this)} className={classes.toolButton} variant="contained"
                        color="primary">
                    Add Image
                </Button>
                <DropzoneAreaBase
                    acceptedFiles={['image/*']}
                    dropzoneText={"Drag and drop an image here or click"}
                    onChange={(files) => console.log('Files:', files)}
                    onAlert={(message, variant) => console.log(`${variant}: ${message}`)}
                />
                <div className="button">
                    { this.state.visible ? <Button variant="contained" color="primary" onClick={this.handleSave.bind(this)}>
                        Submit
                    </Button> :null}
                </div>
            </div>
        );
    }
}
export default withSnackbar(withRouter(withStyles(styles) (DropzoneDialogExample)));
