class Verify extends React.Component {
    handleChange(event) {
        this.setState({ file: event.target.files })
    }

    async handleSubmit(e) {
        e.preventDefault()
        let res = await this.uploadFile(this.state.file);
    }

    async uploadFile(File){
        const formData = new FormData();

        formData.append('image', File)
        axios.post('/api/v1/img/uploadMultipleFiles', formData, {
            headers: {
                'content-type': 'multipart/form-data'
            }
        }).then(response => {
            console.log(response.data)
        });
    }

    render() {
        return (
            <form onSubmit={this.handleSubmit}>
                <input type="file" id="file" multiple name="file" onChange={this.handleChange} />
                <button type="submit" className="btn btn-info"> Update File </button>
            </form>
        )
    }
};

export default withSnackbar(withRouter(withStyles(styles) (Verify)));
