import React from 'react';
import {inject, observer} from "mobx-react";
import Table from "@material-ui/core/Table";
import TableHead from "@material-ui/core/TableHead";
import TableRow from "@material-ui/core/TableRow";
import TableCell from "@material-ui/core/TableCell";
import TableBody from "@material-ui/core/TableBody";
import TableContainer from "@material-ui/core/TableContainer";
import {Button, Grid} from "@material-ui/core";
import AddGroup from "./AddGroup";
import Dialog from "@material-ui/core/Dialog";
import DialogTitle from "@material-ui/core/DialogTitle";
import DialogContent from "@material-ui/core/DialogContent";

@inject('createGroupDialogStore')
@observer
export default class CreateGroupDialog extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            open: false
        }
        this.handleClickOpen = this.handleClickOpen.bind(this)
        this.handleClose = this.handleClose.bind(this);
    }
    handleClickOpen() {
        this.setState({
            open: true
        });
    }

    handleClose() {
        this.setState({
            groupName: '',
            open: false
        })
    }

    componentDidMount() {
        this.props.createGroupDialogStore.loadGroupList();
    }

    render() {
        const {userGroupAuthorityList} = this.props.createGroupDialogStore;
        return (
            <div>
            <Button variant="contained" color="primary" onClick={this.handleClickOpen}>
                그룹 관리
            </Button>
            <Dialog open={this.state.open} onClose={this.handleClose}>
                <DialogTitle>그룹 리스트</DialogTitle>
                <DialogContent>
            <TableContainer style={{maxHeight:300}}>
                <Table stickyHeader size="small" aria-label="a dense table sticky table" >
                    <TableHead>
                        <TableRow>
                            <TableCell>그룹번호</TableCell>
                            <TableCell>그룹명</TableCell>
                            <TableCell>그룹권한</TableCell>
                            <TableCell>생성일</TableCell>
                            <TableCell>수정일</TableCell>
                        </TableRow>
                    </TableHead>
                    <TableBody>
                        {userGroupAuthorityList.length > 0 ?
                            userGroupAuthorityList.map((authority) =>
                                <TableRow key={authority.groupNo}>
                                    <TableCell>
                                        {authority.groupNo}
                                    </TableCell>
                                    <TableCell>
                                        {authority.groupName}
                                    </TableCell>
                                    <TableCell>
                                        {authority.authorityName}
                                    </TableCell>
                                    <TableCell>
                                        {authority.createdDatetime}
                                    </TableCell>
                                    <TableCell>
                                        {authority.updatedDatetime}
                                    </TableCell>

                                </TableRow>
                            )
                            :
                            ''
                        }
                    </TableBody>
                </Table>
            </TableContainer>
        <hr></hr>
                <AddGroup/>
                </DialogContent>
            </Dialog>
        </div>
        );
    }
}
